#include "ac_headfile.h"#include <stdio.h>#include <stdlib.h>#include <iostream>#include <string.h>using namespace std;int main(int argc, char ** argv){	FILE * input  = fopen(argv[1],"rb");	char *content = NULL;	int size = 0;	    if (input)    {			fseek(input, 0L, SEEK_END);		size =  ftell(input);    		fseek(input,0L,SEEK_SET);        cout<<size<<endl;				content = new char[size];				fread(content,1,size,input);		        fclose(input);    }		OutputFile outputfile;	bool success = calDescriptor(content,size,outputfile);		//图片特征计算可能出错	if(success)	{		cout<<"outputLen:"<<outputfile.outputLen<<endl;		FILE * output  = fopen(argv[2],"wb");		if(output)		{			fwrite(outputfile.output,1,outputfile.outputLen,output);			fclose(output);		}	}	else		cout<<"some mistake happened!"<<endl;	delete content;	return 0;}